pragma solidity ^0.5.16;

import "./libraries/SafeMath.sol";
import "./interfaces/IImx.sol";
import "./interfaces/IClaimable.sol";

/*
 * Distributes own balance to recipients according to distributed shares
 * If not 100% of the shares is distributed the default address is the admin
 */

contract TreasuryDistributor is IClaimable {
	using SafeMath for uint;

	address public imx;
	address public admin;
	address public claimable;

	struct Recipient {
		address account;
		uint shares;
	}
	Recipient[] public recipients;
	
	uint public sharesDistributed;
	uint public constant sharesTotal = 1e18;

	constructor (
		address imx_,
		address admin_,
		address claimable_
	) public {
		imx = imx_;
		admin = admin_;
		claimable = claimable_;
	}

	function setAdmin(address admin_) public {
		require(msg.sender == admin, "TreasuryDistributor::setAdmin: unauthorized");
		admin = admin_;
	}

	function addRecipient(address account, uint shares) public {
		require(msg.sender == admin, "TreasuryDistributor::addRecipient: unauthorized");
		require(sharesDistributed.add(shares) <= sharesTotal, "TreasuryDistributor::addRecipient: unauthorized");
		sharesDistributed += shares;
		
		for (uint i = 0; i < recipients.length; i++) {
			if (recipients[i].account == account) {
				recipients[i].shares += shares;
			}
		}
		
		recipients.push(Recipient(account, shares));
	}

	function claim() public nonReentrant {
		IClaimable(claimable).claim();
		uint amount = IImx(imx).balanceOf(address(this));
		
		for (uint i = 0; i < recipients.length; i++) {
			IImx(imx).transfer( recipients[i].account, amount.mul(recipients[i].shares).div(sharesTotal) );
		}
		
		amount = IImx(imx).balanceOf(address(this));
		IImx(imx).transfer(admin, amount);
	}
	
	// Prevents a contract from calling itself, directly or indirectly.
	bool internal _notEntered = true;
	modifier nonReentrant() {
		require(_notEntered, "TreasuryDistributor: REENTERED");
		_notEntered = false;
		_;
		_notEntered = true;
	}
}
