pragma solidity ^0.5.16;

import "./libraries/SafeMath.sol";
import "./interfaces/IImx.sol";
import "./interfaces/IClaimable.sol";

contract TreasuryVesterV2 is IClaimable {
	using SafeMath for uint;

	address public imx;
	address public recipient;

	uint public vestingAmount;
	uint public vestingBegin;
	uint public vestingEnd;

	uint public previousPoint;
	uint public finalPoint = vestingCurve(1e18);
	
	function vestingCurve(uint x) private pure returns (uint y) {
		uint speed = 1e18;
		for (uint i = 0; i < 100e16; i += 1e16) {
			if (x < i + 1e16) return y + (x - i).mul(speed).div(1e18);
			y += speed.mul(1e16);
			speed = speed.mul(98).div(100);
		}
	}

	constructor(
		address imx_,
		address recipient_,
		uint vestingAmount_,
		uint vestingBegin_,
		uint vestingEnd_
	) public {
		require(vestingBegin_ >= block.timestamp, "TreasuryVester::constructor: vesting begin too early");
		require(vestingEnd_ > vestingBegin_, "TreasuryVester::constructor: end is too early");

		imx = imx_;
		recipient = recipient_;

		vestingAmount = vestingAmount_;
		vestingBegin = vestingBegin_;
		vestingEnd = vestingEnd_;

		previousPoint = vestingCurve(0);
	}

	function setRecipient(address recipient_) public {
		require(msg.sender == recipient, "TreasuryVester::setRecipient: unauthorized");
		recipient = recipient_;
	}

	function claim() public {
		require(block.timestamp >= vestingBegin, "TreasuryVester::claim: not time yet");
		uint amount;
		if (block.timestamp >= vestingEnd) {
			amount = IImx(imx).balanceOf(address(this));
		} else {
			uint currentPoint = vestingCurve( (block.timestamp - vestingBegin).mul(1e18).div(vestingEnd - vestingBegin) );
			amount = vestingAmount.mul(currentPoint - previousPoint).div(finalPoint);
			previousPoint = currentPoint;
		}
		IImx(imx).transfer(recipient, amount);
	}
}
